CREATE TABLE cars_table (
car_id INTEGER UNSIGNED NOT NULL AUTO_INCREMENT,
car_make varchar(40) NOT NULL,
car_model varchar(40) NOT NULL,
car_year INTEGER(10) NOT NULL,
dealer_price DECIMAL(20, 2) NOT NULL,
cert_price DECIMAL(20, 2) NOT NULL,
private_price DECIMAL(20, 2) NOT NULL,
PRIMARY KEY (car_id)
);


